import { useEffect, useState, useContext, useRef } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import "./FaturasPagas.css";

const FaturasPagas = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [faturas, setFaturas] = useState([]);
  const [cardAtivo, setCardAtivo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [menuVisivel, setMenuVisivel] = useState(false);
  const menuRef = useRef();

  useEffect(() => {
    if (!user || !user.id) {
      setError("Usu√°rio n√£o autenticado.");
      setLoading(false);
      return;
    }

    const fetchFaturas = async () => {
      try {
        setLoading(true);
        setError(null);
        const response = await fetch(
          `${process.env.REACT_APP_FATURA_URL}/faturas-pagas/${user.id}`
        );
        if (!response.ok) throw new Error("Erro ao buscar faturas pagas.");
        const data = await response.json();
        if (!Array.isArray(data)) throw new Error("Dados inv√°lidos.");
        setFaturas(data);
      } catch (err) {
        console.error("Erro:", err.message);
        setError(err.message);
        setFaturas([]);
      } finally {
        setLoading(false);
      }
    };

    fetchFaturas();
  }, [user]);

  const alternarCard = (id) => {
    setCardAtivo(cardAtivo === id ? null : id);
  };

  const handleClickForaDoMenu = (event) => {
    if (menuRef.current && !menuRef.current.contains(event.target)) {
      setMenuVisivel(false);
    }
  };

  useEffect(() => {
    if (menuVisivel) {
      document.addEventListener("mousedown", handleClickForaDoMenu);
    } else {
      document.removeEventListener("mousedown", handleClickForaDoMenu);
    }

    return () => {
      document.removeEventListener("mousedown", handleClickForaDoMenu);
    };
  }, [menuVisivel]);

  return (
    <div className="faturas-page">
      <header className="faturas-header">
        <div className="header-esquerda">
          <button className="menu-icone" onClick={() => setMenuVisivel(!menuVisivel)}>
            üìã
          </button>
          <span className="header-icone">üí∞</span>
          <h2>Faturas Pagas</h2>
        </div>
        <button className="btn-voltar" onClick={() => navigate("/inicio")}>
          ‚¨ÖÔ∏è In√≠cio
        </button>
      </header>

      {menuVisivel && (
        <div ref={menuRef} className={`menu-lateral ${menuVisivel ? "ativo" : ""}`}>
          <button className="fechar-menu" onClick={() => setMenuVisivel(false)}>
            ‚úñÔ∏è
          </button>
          <ul>
            <li onClick={() => navigate("/inicio")}>üè† In√≠cio</li>
            <li onClick={() => navigate("/faturas-pagas")}>üí∞ Faturas Pagas</li>
            <li onClick={() => navigate("/faturas-atraso")}>‚è≥ Faturas em Atraso</li>
            <li onClick={() => navigate("/faturas-aberto")}>üìÑ Faturas em Aberto</li>
            <li onClick={() => navigate("/notas-fiscais")}>üßæ Notas Fiscais</li>
            <li onClick={() => navigate("/configuracoes")}>‚öôÔ∏è Configura√ß√µes</li>
          </ul>
        </div>
      )}

      {loading ? (
        <p>Carregando...</p>
      ) : error ? (
        <p className="error">{error}</p>
      ) : faturas.length > 0 ? (
        <div className="lista-faturas">
          {faturas.map((fatura) => (
            <div
              key={fatura.id}
              className={`fatura-card ${cardAtivo === fatura.id ? "ativo" : ""}`}
              onClick={() => alternarCard(fatura.id)}
            >
              <div className="fatura-info">
                <span className="numero-fatura">üìÑ {fatura.numero_fatura}</span>
                <span className="valor-fatura">üíµ R$ {parseFloat(fatura.valor).toFixed(2)}</span>
              </div>
              <div className="fatura-detalhes">
                <span>üìÖ {new Date(fatura.data_emissao).toLocaleDateString()}</span>
                <span className="status pago">‚úîÔ∏è Pago</span>
              </div>
              {cardAtivo === fatura.id && (
                <div className="fatura-extra">
                  <p><strong>Data de Vencimento:</strong> {new Date(fatura.data_emissao).toLocaleDateString()}</p>
                  <p><strong>Valor Original:</strong> R$ {parseFloat(fatura.valor).toFixed(2)}</p>
                  <p><strong>Juros Pagos:</strong> R$ {parseFloat(fatura.juros).toFixed(2)}</p>
                  <p><strong>Data de Liquida√ß√£o:</strong> {new Date(fatura.data_liquidacao).toLocaleDateString()}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      ) : (
        <p>Nenhuma fatura encontrada.</p>
      )}
    </div>
  );
};

export default FaturasPagas;
