import { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import MenuLateral from "../components/MenuLateral";
import "./FaturasPagas.css";

const FaturasPagas = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [faturas, setFaturas] = useState([]);
  const [cardAtivo, setCardAtivo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [menuAberto, setMenuAberto] = useState(false);

  useEffect(() => {
    if (!user || !user.id) {
      setError("Usuário não autenticado.");
      setLoading(false);
      return;
    }

    const fetchFaturas = async () => {
      try {
        const response = await fetch(`${process.env.REACT_APP_FATURA_URL}/faturas-pagas/${user.id}`);
        if (!response.ok) throw new Error("Erro ao buscar faturas pagas.");
        const data = await response.json();
        setFaturas(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchFaturas();
  }, [user]);

  const toggleCard = (index) => {
    setCardAtivo(cardAtivo === index ? null : index);
  };

  if (loading) return <p>Carregando faturas...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="pagina-faturas">
      <MenuLateral aberto={menuAberto} setAberto={setMenuAberto} />
      <div className="conteudo">
        <h2>Faturas Pagas</h2>
        {faturas.length === 0 ? (
          <p>Nenhuma fatura paga encontrada.</p>
        ) : (
          faturas.map((fatura, index) => (
            <div
              key={index}
              className={`card-fatura ${cardAtivo === index ? "ativo" : ""}`}
              onClick={() => toggleCard(index)}
            >
              <div className="fatura-cabecalho">
                <p><strong>Número:</strong> {fatura.numero_fatura}</p>
                <p><strong>Valor:</strong> R$ {parseFloat(fatura.valor).toFixed(2)}</p>
              </div>
              <p><strong>Data de Emissão:</strong> {new Date(fatura.data_emissao).toLocaleDateString()}</p>
              <p><strong>Data de Liquidação:</strong> {new Date(fatura.data_liquidacao).toLocaleDateString()}</p>

              {cardAtivo === index && (
                <div className="fatura-links">
                  {fatura.boleto_url && (
                    <a
                      href={fatura.boleto_url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="botao-link"
                    >
                      📎 Ver Boleto
                    </a>
                  )}
                  {fatura.nota_fiscal_url && (
                    <a
                      href={fatura.nota_fiscal_url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="botao-link"
                    >
                      📄 Ver Nota Fiscal
                    </a>
                  )}
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default FaturasPagas;
