import { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import MenuLateral from "../components/MenuLateral";
import "./FaturasPagas.css";

const FaturasPagas = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [faturas, setFaturas] = useState([]);
  const [cardAtivo, setCardAtivo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [menuAberto, setMenuAberto] = useState(false);

  useEffect(() => {
    if (!user || !user.id) {
      setError("Usuário não autenticado.");
      setLoading(false);
      return;
    }

    const fetchFaturas = async () => {
      try {
        const response = await fetch(`${process.env.REACT_APP_FATURA_URL}/faturas-pagas/${user.id}`);
        if (!response.ok) throw new Error("Erro ao buscar faturas pagas.");
        const data = await response.json();
        if (!Array.isArray(data)) throw new Error("Dados inválidos.");
        setFaturas(data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchFaturas();
  }, [user]);

  const alternarCard = (id) => {
    setCardAtivo(cardAtivo === id ? null : id);
  };

  return (
    <div className="faturas-page">
      {/* MENU REUTILIZÁVEL PARA VER SE PARA DE BAGUNÇAR O INICIOOOO*/}
      <MenuLateral visivel={menuAberto} />

      <header className="faturas-header">
        <div className="header-esquerda">
          <button className="btn-menu" onClick={() => setMenuAberto(!menuAberto)}>📑</button>
          <h2>Faturas Pagas</h2>
        </div>
        <button className="btn-voltar" onClick={() => navigate("/inicio")}>⬅️ Início</button>
      </header>

      {loading ? (
        <p>Carregando...</p>
      ) : error ? (
        <p className="error">{error}</p>
      ) : faturas.length > 0 ? (
        <div className="lista-faturas">
          {faturas.map((fatura) => (
            <div key={fatura.id} className={`fatura-card ${cardAtivo === fatura.id ? "ativo" : ""}`} onClick={() => alternarCard(fatura.id)}>
              <div className="fatura-info">
                <span className="numero-fatura">📄 {fatura.numero_fatura}</span>
                <span className="valor-fatura">💵 R$ {parseFloat(fatura.valor).toFixed(2)}</span>
              </div>
              <div className="fatura-detalhes">
                <span>📅 {new Date(fatura.data_emissao).toLocaleDateString()}</span>
                <span className="status pago">✔️ Pago</span>
              </div>
              {cardAtivo === fatura.id && (
                <div className="fatura-extra">
                  <p><strong>Data de Vencimento:</strong> {new Date(fatura.data_emissao).toLocaleDateString()}</p>
                  <p><strong>Valor Original:</strong> R$ {parseFloat(fatura.valor).toFixed(2)}</p>
                  <p><strong>Juros Pagos:</strong> R$ {parseFloat(fatura.juros).toFixed(2)}</p>
                  <p><strong>Data de Liquidação:</strong> {new Date(fatura.data_liquidacao).toLocaleDateString()}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      ) : (
        <p>Nenhuma fatura encontrada.</p>
      )}
    </div>
  );
};

export default FaturasPagas;
