import { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import MenuLateral from "../components/MenuLateral";
import "./FaturasPagas.css";

const FaturasPagas = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [faturas, setFaturas] = useState([]);
  const [cardAtivo, setCardAtivo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [menuAberto, setMenuAberto] = useState(false);

  const [mesSelecionado, setMesSelecionado] = useState("");
  const [anoSelecionado, setAnoSelecionado] = useState("");
  const [paginaAtual, setPaginaAtual] = useState(1);
  const [limitePorPagina] = useState(5); // limite definido por pÃ¡gina

  const meses = [
    { value: "", label: "Todos os meses" },
    { value: "1", label: "Janeiro" },
    { value: "2", label: "Fevereiro" },
    { value: "3", label: "MarÃ§o" },
    { value: "4", label: "Abril" },
    { value: "5", label: "Maio" },
    { value: "6", label: "Junho" },
    { value: "7", label: "Julho" },
    { value: "8", label: "Agosto" },
    { value: "9", label: "Setembro" },
    { value: "10", label: "Outubro" },
    { value: "11", label: "Novembro" },
    { value: "12", label: "Dezembro" },
  ];

  const anos = ["", 2023, 2024, 2025];

  const buscarFaturas = async () => {
    setLoading(true);
    setError(null);
    try {
      const query = new URLSearchParams();
      if (mesSelecionado) query.append("mes", mesSelecionado);
      if (anoSelecionado) query.append("ano", anoSelecionado);
      query.append("page", paginaAtual);
      query.append("limit", limitePorPagina);

      console.log("ðŸ”Ž Enviando filtro:", {
        mes: mesSelecionado,
        ano: anoSelecionado,
        paginaAtual,
        url: `${process.env.REACT_APP_FATURA_URL}/faturas-pagas/${user.id}?${query.toString()}`
      });

      const response = await fetch(
        `${process.env.REACT_APP_FATURA_URL}/faturas-pagas/${user.id}?${query.toString()}`
      );
      if (!response.ok) throw new Error("Erro ao buscar faturas pagas.");
      const data = await response.json();
      setFaturas(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (user && user.id) buscarFaturas();
  }, [user, paginaAtual]); // atualizar quando a pÃ¡gina mudar

  const toggleCard = (numeroFatura) => {
    setCardAtivo(cardAtivo === numeroFatura ? null : numeroFatura);
  };

  const aplicarFiltro = () => {
    setPaginaAtual(1); // resetar para pÃ¡gina 1 ao aplicar novo filtro
    buscarFaturas();
  };

  if (loading) return <p>Carregando faturas...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="pagina-faturas">
      <MenuLateral aberto={menuAberto} setAberto={setMenuAberto} />

      <div className="faturas-page">
        <div className="faturas-header">
          <div className="header-esquerda">
            <button className="btn-voltar" onClick={() => navigate("/inicio")}>Voltar</button>
            <h2>Faturas Pagas</h2>
          </div>
          <button className="btn-menu" onClick={() => setMenuAberto(!menuAberto)}>â˜°</button>
        </div>

        {/* Filtros */}
        <div style={{ display: "flex", gap: "1rem", marginBottom: "1.5rem" }}>
          <select value={mesSelecionado} onChange={(e) => setMesSelecionado(e.target.value)}>
            {meses.map((m) => (
              <option key={m.value} value={m.value}>{m.label}</option>
            ))}
          </select>

          <select value={anoSelecionado} onChange={(e) => setAnoSelecionado(e.target.value)}>
            {anos.map((a) => (
              <option key={a} value={a}>{a === "" ? "Todos os anos" : a}</option>
            ))}
          </select>

          <button onClick={aplicarFiltro}>Filtrar</button>
        </div>

        {/* Lista de faturas */}
        <div className="lista-faturas">
          {faturas.length === 0 ? (
            <p>Nenhuma fatura paga encontrada.</p>
          ) : (
            faturas.map((fatura) => (
              <div
                key={fatura.numero_fatura}
                className={`fatura-card ${cardAtivo === fatura.numero_fatura ? "ativo" : ""}`}
                onClick={() => toggleCard(fatura.numero_fatura)}
              >
                <div className="fatura-info">
                  <span><strong>NÂº:</strong> {fatura.numero_fatura}</span>
                  <span><strong>Valor:</strong> R$ {parseFloat(fatura.valor).toFixed(2)}</span>
                </div>

                <div className="fatura-detalhes">
                  <span><strong>EmissÃ£o:</strong> {new Date(fatura.data_emissao).toLocaleDateString()}</span>
                  <span><strong>LiquidaÃ§Ã£o:</strong> {new Date(fatura.data_liquidacao).toLocaleDateString()}</span>
                  <span><strong>Juros Pagos:</strong> R$ {parseFloat(fatura.juros).toFixed(2)}</span>
                  <span className="status pago">Pago</span>
                </div>

                {cardAtivo === fatura.numero_fatura && (
                  <div className="fatura-extra">
                    {fatura.boleto_url && (
                      <a href={fatura.boleto_url} target="_blank" rel="noopener noreferrer" className="botao-link">
                        Ver Boleto
                      </a>
                    )}
                    {fatura.nota_fiscal_url && (
                      <a href={fatura.nota_fiscal_url} target="_blank" rel="noopener noreferrer" className="botao-link">
                        Ver Nota Fiscal
                      </a>
                    )}
                  </div>
                )}
              </div>
            ))
          )}
        </div>

        {/* Controles de PaginaÃ§Ã£o */}
        <div style={{ display: "flex", justifyContent: "center", gap: "1rem", marginTop: "2rem" }}>
          <button disabled={paginaAtual === 1} onClick={() => setPaginaAtual(paginaAtual - 1)}>
            PÃ¡gina Anterior
          </button>
          <span>PÃ¡gina {paginaAtual}</span>
          <button disabled={faturas.length < limitePorPagina} onClick={() => setPaginaAtual(paginaAtual + 1)}>
            PrÃ³xima PÃ¡gina
          </button>
        </div>
      </div>
    </div>
  );
};

export default FaturasPagas;
