import React, { useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { Link } from "react-router-dom";
import "./Inicio.css";

const Inicio = () => {
  const { user } = useContext(AuthContext);

  const faturasRecentes = [
    {
      id: 1,
      icone: "üí∞",
      texto: "Fatura de Mar√ßo paga com sucesso!",
      tempo: "15 minutos atr√°s",
    },
    {
      id: 2,
      icone: "‚è≥",
      texto: "Fatura de Abril em atraso! Evite juros.",
      tempo: "1 hora atr√°s",
    },
    {
      id: 3,
      icone: "üìÑ",
      texto: "Nova fatura emitida para Maio.",
      tempo: "2 horas atr√°s",
    },
  ];

  return (
    <div className="pagina-inicio">
      {/* Menu lateral fixo */}
      <aside className="menu-inicio">
        <ul>
          <li><Link to="/inicio">üè† In√≠cio</Link></li>
          <li><Link to={`/faturas-pagas/${user?.id}`}>üí∞ Faturas Pagas</Link></li>
          <li><Link to="#">‚è≥ Faturas em Atraso</Link></li>
          <li><Link to="#">üìÑ Faturas em Aberto</Link></li>
          <li><Link to="#">üßæ Notas Fiscais</Link></li>
          <li><Link to="#">‚öôÔ∏è Configura√ß√µes</Link></li>
        </ul>
      </aside>

      {/* Conte√∫do principal */}
      <main className="inicio-container">
        <div className="perfil-box">
          <div className="info">
            <h2>{user?.name || "Teste Usu√°rio"}</h2>
            <span>@{user?.email?.split("@")[0] || "username"}</span>
          </div>
          <button className="editar-btn">Editar Perfil</button>
        </div>

        <div className="ultimas-faturas">
          <h3>√öltimas Faturas</h3>
          {faturasRecentes.map((fatura) => (
            <div key={fatura.id} className="card-fatura">
              <span>{fatura.icone} {fatura.texto}</span>
              <span>{fatura.tempo}</span>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
};

export default Inicio;
