import { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import "./FaturasPagas.css";

const FaturasPagas = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [faturas, setFaturas] = useState([]);
  const [cardAtivo, setCardAtivo] = useState(null);
  const [mostrarMenu, setMostrarMenu] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchFaturas = async () => {
      if (!user || !user.id) {
        setError("Usu√°rio n√£o autenticado.");
        setLoading(false);
        return;
      }

      try {
        setLoading(true);
        const response = await fetch(`${process.env.REACT_APP_FATURA_URL}/faturas-pagas/${user.id}`);
        if (!response.ok) throw new Error("Erro ao buscar faturas pagas.");
        const data = await response.json();
        if (!Array.isArray(data)) throw new Error("Dados inv√°lidos.");
        setFaturas(data);
      } catch (err) {
        console.error("Erro:", err.message);
        setError(err.message);
        setFaturas([]);
      } finally {
        setLoading(false);
      }
    };

    fetchFaturas();
  }, [user]);

  const alternarCard = (id) => {
    setCardAtivo(cardAtivo === id ? null : id);
  };

  const toggleMenu = () => {
    setMostrarMenu(!mostrarMenu);
  };

  const navegar = (rota) => {
    navigate(rota);
    setMostrarMenu(false);
  };

  return (
    <div className="faturas-page">
      <header className="faturas-header">
        <div className="header-esquerda">
          <button className="menu-icone" onClick={toggleMenu}>‚ò∞</button>
          <span className="header-icone">üí∞</span>
          <h2>Faturas Pagas</h2>
        </div>
        <button className="btn-voltar" onClick={() => navigate("/inicio")}>‚¨ÖÔ∏è In√≠cio</button>
      </header>

      {mostrarMenu && (
        <div className="menu-lateral">
          <ul>
            <li onClick={() => navegar("/inicio")}>üè† In√≠cio</li>
            <li onClick={() => navegar("/faturas-pagas")}>üí∞ Faturas Pagas</li>
            <li onClick={() => navegar("/faturas-atraso")}>‚è≥ Faturas em Atraso</li>
            <li onClick={() => navegar("/faturas-aberto")}>üìÑ Faturas em Aberto</li>
            <li onClick={() => navegar("/notas-fiscais")}>üßæ Notas Fiscais</li>
          </ul>
        </div>
      )}

      {loading ? (
        <p>Carregando...</p>
      ) : error ? (
        <p className="error">{error}</p>
      ) : faturas.length > 0 ? (
        <div className="lista-faturas">
          {faturas.map((fatura) => (
            <div
              key={fatura.id}
              className={`fatura-card ${cardAtivo === fatura.id ? "ativo" : ""}`}
              onClick={() => alternarCard(fatura.id)}
            >
              <div className="fatura-info">
                <span className="numero-fatura">üìÑ {fatura.numero_fatura}</span>
                <span className="valor-fatura">üíµ R$ {parseFloat(fatura.valor).toFixed(2)}</span>
              </div>
              <div className="fatura-detalhes">
                <span>üìÖ {new Date(fatura.data_emissao).toLocaleDateString()}</span>
                <span className="status pago">‚úîÔ∏è Pago</span>
              </div>
              {cardAtivo === fatura.id && (
                <div className="fatura-extra">
                  <p><strong>Data de Vencimento:</strong> {new Date(fatura.data_emissao).toLocaleDateString()}</p>
                  <p><strong>Valor Original:</strong> R$ {parseFloat(fatura.valor).toFixed(2)}</p>
                  <p><strong>Juros Pagos:</strong> R$ {parseFloat(fatura.juros).toFixed(2)}</p>
                  <p><strong>Data de Liquida√ß√£o:</strong> {new Date(fatura.data_liquidacao).toLocaleDateString()}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      ) : (
        <p>Nenhuma fatura encontrada.</p>
      )}
    </div>
  );
};

export default FaturasPagas;
