import { useEffect, useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import "./FaturasPagas.css";

const FaturasPagas = () => {
  const { user } = useContext(AuthContext);
  const navigate = useNavigate();
  const [faturas, setFaturas] = useState([]);
  const [cardAtivo, setCardAtivo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!user || !user.id) {
      console.warn("Usu치rio n칚o autenticado.");
      setError("Usu치rio n칚o autenticado.");
      setLoading(false);
      return;
    }

    console.log("ID do usu치rio logado:", user.id); // Debug 칰til

    const fetchFaturas = async () => {
      try {
        setLoading(true);
        setError(null);

        const response = await fetch(
          `${process.env.REACT_APP_FATURA_URL}/faturas-pagas/${user.id}`
        );

        if (!response.ok) throw new Error("Erro ao buscar faturas pagas.");

        const data = await response.json();
        if (!Array.isArray(data)) throw new Error("Dados inv치lidos.");

        setFaturas(data);
      } catch (err) {
        console.error("Erro:", err.message);
        setError(err.message);
        setFaturas([]);
      } finally {
        setLoading(false);
      }
    };

    fetchFaturas();
  }, [user]);

  const alternarCard = (id) => {
    setCardAtivo(cardAtivo === id ? null : id);
  };

  return (
    <div className="faturas-page">
      <header className="faturas-header">
        <div className="header-esquerda">
          <span className="header-icone">游눯</span>
          <h2>Faturas Pagas</h2>
        </div>
        <button className="btn-voltar" onClick={() => navigate("/inicio")}>
          拘勇 In칤cio
        </button>
      </header>

      {loading ? (
        <p>Carregando...</p>
      ) : error ? (
        <p className="error">{error}</p>
      ) : faturas.length > 0 ? (
        <div className="lista-faturas">
          {faturas.map((fatura) => (
            <div
              key={fatura.id}
              className={`fatura-card ${cardAtivo === fatura.id ? "ativo" : ""}`}
              onClick={() => alternarCard(fatura.id)}
            >
              <div className="fatura-info">
                <span className="numero-fatura">游늯 {fatura.numero_fatura}</span>
                <span className="valor-fatura">
                  游눳 R$ {parseFloat(fatura.valor).toFixed(2)}
                </span>
              </div>
              <div className="fatura-detalhes">
                <span>游늰 {new Date(fatura.data_emissao).toLocaleDateString()}</span>
                <span className="status pago">九덢잺 Pago</span>
              </div>
              {cardAtivo === fatura.id && (
                <div className="fatura-extra">
                  <p><strong>Data de Vencimento:</strong> {new Date(fatura.data_emissao).toLocaleDateString()}</p>
                  <p><strong>Valor Original:</strong> R$ {parseFloat(fatura.valor).toFixed(2)}</p>
                  <p><strong>Juros Pagos:</strong> R$ {parseFloat(fatura.juros).toFixed(2)}</p>
                  <p><strong>Data de Liquida칞칚o:</strong> {new Date(fatura.data_liquidacao).toLocaleDateString()}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      ) : (
        <p>Nenhuma fatura encontrada.</p>
      )}
    </div>
  );
};

export default FaturasPagas;
