import { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./FaturasPagas.css";

const FaturasPagas = () => {
  const { clienteId } = useParams();
  const navigate = useNavigate();
  const [faturas, setFaturas] = useState([]);
  const [cardAtivo, setCardAtivo] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchFaturas = async () => {
      try {
        if (!clienteId) throw new Error("ID do cliente n√£o fornecido.");

        const response = await fetch(`http://localhost:5001/faturas-pagas/${clienteId}`);
        if (!response.ok) throw new Error("Erro ao buscar faturas pagas.");
        const data = await response.json();
        if (!Array.isArray(data)) throw new Error("Resposta inv√°lida da API.");
        setFaturas(data);
      } catch (err) {
        console.error("Erro:", err.message);
        setError(err.message);
        setFaturas([]);
      } finally {
        setLoading(false);
      }
    };

    fetchFaturas();
  }, [clienteId]);

  const alternarCard = (id) => {
    setCardAtivo(cardAtivo === id ? null : id);
  };

  return (
    <div className="faturas-page">
      <div className="faturas-topo">
        <h2>üí∞ Faturas Pagas</h2>
        <button className="btn-voltar" onClick={() => navigate("/inicio")}>
          ‚¨ÖÔ∏è In√≠cio
        </button>
      </div>

      {loading ? (
        <p>Carregando faturas...</p>
      ) : error ? (
        <p className="error">{error}</p>
      ) : faturas.length > 0 ? (
        <div className="lista-faturas">
          {faturas.map((fatura) => (
            <div
              key={fatura.id}
              className={`fatura-card ${cardAtivo === fatura.id ? "ativo" : ""}`}
              onClick={() => alternarCard(fatura.id)}
            >
              <div className="fatura-info">
                <span className="numero-fatura">üìÑ {fatura.numero_fatura}</span>
                <span className="valor-fatura">
                  üíµ R$ {parseFloat(fatura.valor).toFixed(2)}
                </span>
              </div>

              <div className="fatura-detalhes">
                <span>üìÖ {new Date(fatura.data_emissao).toLocaleDateString()}</span>
                <span className="status pago">‚úîÔ∏è Pago</span>
              </div>

              {cardAtivo === fatura.id && (
                <div className="fatura-extra">
                  <p><strong>Data de Emiss√£o:</strong> {new Date(fatura.data_emissao).toLocaleDateString()}</p>
                  <p><strong>Valor:</strong> R$ {parseFloat(fatura.valor).toFixed(2)}</p>
                  <p><strong>Juros Pagos:</strong> R$ {parseFloat(fatura.juros).toFixed(2)}</p>
                  <p><strong>Data de Liquida√ß√£o:</strong> {new Date(fatura.data_liquidacao).toLocaleDateString()}</p>
                </div>
              )}
            </div>
          ))}
        </div>
      ) : (
        <p>Nenhuma fatura encontrada.</p>
      )}
    </div>
  );
};

export default FaturasPagas;
